mapper.insert:
insert into poms_config_user (id, name, password, 
  disable_flag, operate_time, last_login_time
  )
values (#{id,jdbcType=SMALLINT}, #{name,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{disableFlag,jdbcType=BIT}, #{operateTime,jdbcType=TIMESTAMP}, #{lastLoginTime,jdbcType=TIMESTAMP}
  )

mapper.select:
SELECT id, `name`, password, disable_flag AS disableFlag, DATE_FORMAT(operate_time, '%Y-%m-%d %H:%i:%s') AS operateTime, operator, DATE_FORMAT(last_login_time, '%Y-%m-%d %H:%i:%s') AS lastLoginTime FROM poms_config_user
AND `name` LIKE CONCAT('%', #{userReqBean.name}, '%')
AND disable_flag = #{userReqBean.disableFlag}
ORDER BY ${userReqBean.orderBy} ${userReqBean.sortType}
ORDER BY id DESC

mapper.update:
UPDATE poms_config_user
`name` = #{userReqBean.name}, password = #{userReqBean.password}, operator = #{userReqBean.operator},
WHERE id = #{userReqBean.id}

UPDATE poms_config_user
SET disable_flag = 1
WHERE id = #{userId}

UPDATE poms_config_user
SET disable_flag = 0
WHERE id = #{userId}

mapper.select:
SELECT COUNT(id)
FROM poms_config_user
WHERE `name` = #{userName}

  AND id != #{userId}
